(function() {
  var Q, updateProgress;

  Q = require("q");

  updateProgress = function(deferred) {
    return function(e) {
      if (e.lengthComputable) {
        return deferred.notify(e.loaded / e.total);
      }
    };
  };

  module.exports = {
    post: function(url, formData) {
      var deferred, request;
      deferred = Q.defer();
      request = new XMLHttpRequest();
      request.open("POST", url, true);
      request.upload.onprogress = updateProgress(deferred);
      request.onreadystatechange = function(e) {
        if (request.readyState === 4) {
          if (isSuccess(request)) {
            return deferred.resolve(true);
          } else {
            return deferred.reject(request.responseText);
          }
        }
      };
      request.send(formData);
      return deferred.promise;
    },
    getJSON: function() {},
    get: function() {}
  };

}).call(this);
